v2((data, context) => {
  if (!data.FilterMainOptions) return data.default;
  const lines = data.default.split('\n');
  // only keep lines with 1 code like l,r,t etc.
  const onlyMain = lines.filter(v => v.indexOf(':') == 1);
  return onlyMain.join('\n');
});

v2((data, context) => {
  const t = data.Title;
  console.log('Title at start', t);

  // Check if empty
  if (t == null || (/^\s*$/).test(t))
    return {
      value: { severity: 'error', message: 'this needs real text'}
    };
  // Show warning if not all lower case
  if (data.Title.toLowerCase() !== data.Title)
    return { value: { severity: "warning", message: "we suggest to use lower case only."} };
  return data.value;
});

