v2((data) => {
  // Start with the default list, as it could have changed
  const lines = data.default.match(/[^\r\n]+/g);

  // Filter and join again
  const keep = lines.filter(l => l.startsWith(data.Country));
  return keep.join('\n');
});

v2((data) => {
  // don't change if already has a value
  if (data.value) return data.value;
  // if it's empty, get the current date/time
  return new Date();
});

v2((data) => {
  const now = new Date();
  const add = data.PublishIn || 0;
  const offset = -now.getTimezoneOffset() / 60;
  return now.setHours(now.getHours() + add + offset);
});

