      { name: "Slave2", value: "Slave2 also set by master"}
    ]
  }
});

v2((data, context) => {
  return data.default
    .replace('[name]', context.user.name ?? "unknown")
    .replace('[userid]', context.user.id)
    .replace('[isanonymous]', context.user.isAnonymous)
    .replace('[issiteadmin]', context.user.isSiteAdmin);
});

